import photo from '../assets/sora.jpg'
import photo2 from '../assets/back2.png'
import {signOut } from "firebase/auth";
import { auth } from "../firebase/firebase_config";
import { CheckBox, Input } from 'react-native-elements';
import * as yup from 'yup'
import { Formik } from 'formik';
import Icon from 'react-native-vector-icons/FontAwesome';

import { StyleSheet, 
    TouchableOpacity,
    Button,
    Text,
    View,
   TextInput ,
   Image,
   FlatList,
   ImageBackground ,
   ScrollView,
   Dimensions
 } from 'react-native';
import { globalstyles } from "../styles/global";
import { border, borderBottom } from "styled-system";
const loginValidationSchema = yup.object().shape({
  email: yup.string().email('Please enter valid email').required('Email Address is required'),
  password: yup.string().min(8, ({ min }) => `password must be at least ${min}characters `)
    .required('password is required').matches(
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})/,
      "Must Contain 8 Characters, One Uppercase, One Lowercase, One Number and One Special Case Character"
    ),
});

