      if(values.email == ''){
        showMessage({
          message: 'Lütfen Alanları Doldurunuz.',
          type: "danger",
        });
      }
      else{
          await auth().sendEmailVerification(values.email)
              .then(()=> {
                  showMessage({
                      message: 'E-mailinizi kontrol ediniz.',
                      type: "success",
                    });
                    console.log('YES');
                  navigation.goBack();
              }).catch(function (e) {
