{({ handleChange, handleBlur, handleSubmit, values, touched, errors, isValid }) => (
  <ImageBackground
  source={require('../assets/back2.png')}
  style={{
    height: Dimensions.get('window').height 
  }}>
  <View style={{flex:1,padding:24}}>
    <Text style={{textAlign:'center',marginTop:200 ,
     fontSize:15,fontWeight:'bold',
     fontStyle:'italic',color:'white',marginBottom:30}}>Type Your Email To Reset Password </Text>
    <View floatingLable style={{ borderColor: '#4632A1'}}>
                  <Text style={{color:'white'}}>Email</Text>
                  <View style={{ flexDirection: 'row', alignItems: 'stretch' }}>

                    <Input style={{color:'white'}} onChangeText={handleChange('email')}
                      onBlur={handleBlur('email')} value={values.email}
                       />

                    <Icon name={!errors.email ? 'check' : 'close'} style={{ color: !errors.email ? '#4632A1' : 'red' }} />
                  </View>
                </View>
                <Button title="Reset Password" onPress={handleSubmit}></Button>
  </View>
  </ImageBackground>
  )}


  </Formik>
);
}
